import { Component } from '@angular/core';
import { ModalController, NavController } from '@ionic/angular';
import { AddItemPage } from '../add-item/add-item.page';
import { EditItemPage } from '../edit-item/edit-item.page';
import { Data } from '../data.service';

@Component({
  selector: 'app-home',
  templateUrl: 'home.page.html',
  styleUrls: ['home.page.scss'],
})
export class HomePage {

  public items: { title: string, description: string }[] = [];

  constructor(public modalCtrl: ModalController, private navCtrl: NavController, public dataService: Data) {}

  ionViewWillEnter() { 
    if (this.items.length === 0) {
      this.initializeItems();
    }
  }

  initializeItems() {
    this.dataService.getData().then((todos) => {
      if(todos) {
        this.items = todos;
      }
    });
  }

  async addItem() {
    const modal = await this.modalCtrl.create({
      component: AddItemPage
    });

    modal.onDidDismiss().then((data) => {
      if (data && data.data) {
        this.saveItem(data.data);
      }
    });

    await modal.present();
  }

  saveItem(item: { title: string, description: string }) {
    if (item.title.trim() !== '' && item.description.trim() !== '') {
      this.items.push(item);
      this.dataService.save(this.items);
    }
  }

  async editItem(originalItem: { title: string, description: string }) {
    const modal = await this.modalCtrl.create({
      component: EditItemPage,
      componentProps: {
        originalItem: { ...originalItem }, // Passa o item original
        updatedItem: { title: '', description: '' } // Cria um objeto vazio para o item atualizado
      }
    });

    modal.onDidDismiss().then((data) => {
      if (data && data.data) {
        this.updateItem(originalItem, data.data); // Chama a função updateItem com o item original e o item atualizado
      }
    });

    await modal.present();
  }

  updateItem(originalItem: { title: string, description: string }, updatedItem: { title: string, description: string }) {
    const index = this.items.findIndex(item => item.title === originalItem.title); 

    if (index !== -1) {
      this.items[index] = updatedItem;
      this.dataService.save(this.items);
    }
  }

  viewItem(item: { title: string, description: string }) { 
    this.navCtrl.navigateByUrl(`/item-detail`, { state: { item: item } });
  }

}
<ion-header>
  <ion-toolbar color="secondary">
    <ion-title>
      TO-DO!
    </ion-title>
    <ion-buttons slot="end"> 
      <ion-button (click)="addItem()"> 
        <ion-icon name="add-outline"></ion-icon>
      </ion-button>
    </ion-buttons>
  </ion-toolbar>
</ion-header>

<ion-content>
  <ion-list ion-fixed>
    <ion-item *ngFor="let item of items">
      <ion-label>{{ item.title }}</ion-label>
      <ion-buttons slot="end">
        <ion-button (click)="editItem(item)">Editar</ion-button>
        <!-- <ion-button (click)="deleteItem(item)">Deletar</ion-button> -->
      </ion-buttons>
    </ion-item>
  </ion-list>
</ion-content>
