export class EditItemPage {

  originalItem: { title: string, description: string } = { title: '', description: '' };
  updatedItem: { title: string, description: string } = { title: '', description: '' };
  errorMessage: string = '';

  constructor(public modalCtrl: ModalController, private navParams: NavParams) { }

  ngOnInit() {
    const originalItem = this.navParams.get('originalItem');
    if (originalItem) {
      this.originalItem = { ...originalItem }; // Copia o item original
    }
  }

  close() {
    this.modalCtrl.dismiss();
  }

  editItem() {
    if (this.updatedItem.title.trim() !== '' && this.updatedItem.description.trim() !== '') {
      if (this.originalItem.title !== this.updatedItem.title || this.originalItem.description !== this.updatedItem.description) {
        this.modalCtrl.dismiss(this.updatedItem); // Retorna o item atualizado
      } else {
        this.modalCtrl.dismiss();
      }
    } else {
      this.errorMessage = 'Por favor, preencha todos os campos';
    }
  }
}
