import { Component } from '@angular/core';
import { NavController } from '@ionic/angular';
import { Data } from '../data.service';

@Component({
  selector: 'app-home',
  templateUrl: 'home.page.html',
  styleUrls: ['home.page.scss'],
})
export class HomePage {

  public items: { title: string, description: string }[] = [
    { title: 'Item 1', description: 'Description 1' },
    { title: 'Item 2', description: 'Description 2' },
    { title: 'Item 3', description: 'Description 3' }
  ];

  constructor(private navCtrl: NavController, public dataService: Data) {}

  viewItem(item: { title: string, description: string }) { 
    this.navCtrl.navigateForward(['/item-detail'], { queryParams: { item: JSON.stringify(item) } });
  }
}
<ion-header>
  <ion-toolbar color="secondary">
    <ion-title>
      TO-DO!
    </ion-title>
    <ion-buttons slot="end"> 
      <ion-button (click)="addItem()"> 
        <ion-icon name="add-outline"></ion-icon>
      </ion-button>
    </ion-buttons>
  </ion-toolbar>
</ion-header>

<ion-content>
  <ion-list ion-fixed>
    <ion-item *ngFor="let item of items">
      <ion-label>{{ item.title }}</ion-label>
      <ion-buttons slot="end">
        <ion-button (click)="editItem(item)">Editar</ion-button>
        <!-- <ion-button (click)="deleteItem(item)">Deletar</ion-button> -->
      </ion-buttons>
    </ion-item>
  </ion-list>
</ion-content>
